import React from "react"


import { Heading } from "../common/Heading"
import { contact } from "../data/dummydata"

export const Contact = () => {

  const [mail,setMail] =useState("");
  const [name,setName] =useState("");
  const [subject,setContact] =useState("");
  const [msg,setPName] =useState("");

function sendData(e){
  e.preventDefault();

  const newData = {
    name,
    mail,
    subject,
    msg
  }
  
  axios.post("http://localhost:8070/contact/add",newData).then(()=>{{[
    'success'
    ].map((variant) => (
    <Alert variant="success">data added</Alert>
  ))}
    alert("data added")
  }).catch((err)=>{{[
    'danger'
    ].map((variant) => (
    <Alert>data not added</Alert>
  ))}
    // alert("data not added")
    // console.log(err);
  })

}



  return (
    <>
      <div className='contact'>
        <div className='container'>
          <Heading title='Keep In Touch' />
          <div className='content flexsb'>
            <div className='right'>
              <form onSubmit={sendData}>
                <div className='flex'>
                  <input type='text' placeholder='Name' data-aos='flip-left' onChange={(e)=>{setMail(e.target.value);}}/>
                  <input type='email' placeholder='Email' data-aos='flip-right' onChange={(e)=>{setMail(e.target.value);}}/>
                </div>
                <input type='text' placeholder='Subject' data-aos='flip-up' onChange={(e)=>{setMail(e.target.value);}}/>
                <textarea name='' id='' cols='30' rows='10' data-aos='flip-down' onChange={(e)=>{setMail(e.target.value);}}></textarea>
                <button data-aos='zoom-in-up'>Submit</button>
              </form>
            </div>
            <div className='left'>
              {contact.map((item) => (
                <div className='box' data-aos='zoom-in'>
                  <i>{item.icon}</i>
                  <p>{item.text1}</p>
                  <p>{item.text2}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </>
  )
}
