import React from "react"
import { Heading } from "../common/Heading"
import { contact } from "../data/dummydata"
import emailjs from '@emailjs/browser';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export const Contact = () => {

function sendEmail(e) {
  e.preventDefault();

  emailjs.sendForm(
    'service_rgnoocv',
    'template_deuoujx',
    e.target,
    'mzx4wsWnJQ18NbxVl'
    ).then(res=>{
      console.log(res);
    }).catch(err=>console.log(err));
}


const notify = () => toast.success('helloow', {
  position: "top-right",
  autoClose: 5000,
  hideProgressBar: false,
  closeOnClick: true,
  pauseOnHover: false,
  draggable: true,
  progress: undefined,
  theme: "dark",
  });

  return (
    <>
      <div className='contact'>
        <div className='container'>
          <Heading title='Keep In Touch' />
          <div className='content flexsb'>
            <div className='right'>
              <form onSubmit={sendEmail} >
                <div className='flex'>
                  <input type='text' placeholder='Name' name="name" data-aos='flip-left'/>
                  <input type='email' placeholder='Email' name='email' data-aos='flip-right'/>
                </div>
                <input type='text' placeholder='Subject' name='subject' data-aos='flip-up'/>
                <textarea name='msg' id='' cols='30' rows='10' data-aos='flip-down'></textarea>
                <button data-aos='zoom-in-up' onClick={notify}>Submit</button>
                
              </form>
            </div>
            <div className='left'>
              {contact.map((item) => (
                <div className='box' data-aos='zoom-in'>
                  <i>{item.icon}</i>
                  <p>{item.text1}</p>
                  <p>{item.text2}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </>
  )
}
